import React, { useState, useEffect } from 'react';
import { DndContext } from '@dnd-kit/core';
import Sidebar_Blocks from './components/blocks/Sidebar_Blocks';
import DropArea from './components/blocks/DropArea';
import Mainbar from './components/blocks/Mainbar';
import Sidebar_SavedUnits from './components/units/Sidebar_SavedUnits';
import { createNewBlock, addBlockToTree, deleteBlockFromTree, moveBlockInTree, updateBlockOptionsInTree, computeSums } from './utils/blockUtils';

const availableBlocks = [
  { id: 'aufwaermen', label: 'Aufw채rmen', options: { type: 'switch', unitOptions: ["Distanz", "Zeit"] } },
  { id: 'tempolauf', label: 'Tempolauf', options: { dropdown: ['1RT', '3RT', '5RT', '10RT', 'HMRT', 'MRT'], unitOptions: ["Distanz", "Zeit"] } },
  { id: 'dauerlauf', label: 'Dauerlauf', options: { dropdown: ['Zone 1', 'Zone 2', 'Zone 3', 'Zone 4', 'Zone 5'], unitOptions: ["Distanz", "Zeit"] } },
  { id: 'gehpause', label: 'Gehpause', options: { type: 'switch', unitOptions: ["Distanz", "Zeit"] } },
  { id: 'trabpause', label: 'Trabpause', options: { type: 'switch', unitOptions: ["Distanz", "Zeit"] } },
  { id: 'abkuehlung', label: 'Abk체hlung', options: { type: 'switch', unitOptions: ["Distanz", "Zeit"] } },
  { id: 'wiederholung', label: 'Wiederholung', options: { repetitions: 1 } }
];

function App() {
  const [droppedBlocks, setDroppedBlocks] = useState([]);
  const [instanceCounter, setInstanceCounter] = useState(0);
  const [unitName, setUnitName] = useState('');
  const [unitKuerzel, setUnitKuerzel] = useState('');
  const [savedUnits, setSavedUnits] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/trainingUnits')
      .then(res => res.json())
      .then(data => setSavedUnits(data))
      .catch(err => console.error(err));
  }, []);

  return (
    <DndContext onDragEnd={handleDragEnd}>
      <div className="app-container">
        <Sidebar_SavedUnits savedUnits={savedUnits} onLoad={handleLoad} />
        
        <Mainbar
          droppedBlocks={droppedBlocks}
          setDroppedBlocks={setDroppedBlocks}
          unitName={unitName}
          setUnitName={setUnitName}
          unitKuerzel={unitKuerzel}
          setUnitKuerzel={setUnitKuerzel}
          handleSave={handleSave}/>

        <Sidebar_Blocks availableBlocks={availableBlocks} />
      </div>
    </DndContext>
  );
}

export default App;
