import React from 'react';
import { FaTrash } from 'react-icons/fa';

function SavedUnits({ savedUnits, onLoad }) {
  return (
    <div className="sidebar">
      <h4 className="sidebar-title">Gespeicherte Einheiten</h4>
      <input type="text" className="search-input" placeholder="Suche..." />
      <ul className="unit-list">
        {savedUnits.map(unit => (
          <li key={unit.id} className="unit-item">
            {unit.name} ({unit.kuerzel})
            <div>
              <button onClick={() => onLoad(unit)} className="load-button">
                Laden
              </button>
              <button className="delete-button">
                <FaTrash />
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default SavedUnits;
