import React, { useState, useEffect } from 'react';
import { DndContext } from '@dnd-kit/core';
import Sidebar_Blocks from './components/blocks/Sidebar_Blocks';
import DropArea from './components/blocks/DropArea';
import Mainbar from './components/blocks/Mainbar';
import Sidebar_SavedUnits from './components/units/Sidebar_SavedUnits';
import { createNewBlock, addBlockToTree, deleteBlockFromTree, moveBlockInTree, updateBlockOptionsInTree, computeSums } from './utils/blockUtils';

const availableBlocks = [
  { id: 'aufwaermen', label: 'Aufwärmen', options: { type: 'switch', unitOptions: ["Distanz", "Zeit"] } },
  { id: 'tempolauf', label: 'Tempolauf', options: { dropdown: ['1RT', '3RT', '5RT', '10RT', 'HMRT', 'MRT'], unitOptions: ["Distanz", "Zeit"] } },
  { id: 'dauerlauf', label: 'Dauerlauf', options: { dropdown: ['Zone 1', 'Zone 2', 'Zone 3', 'Zone 4', 'Zone 5'], unitOptions: ["Distanz", "Zeit"] } },
  { id: 'gehpause', label: 'Gehpause', options: { type: 'switch', unitOptions: ["Distanz", "Zeit"] } },
  { id: 'trabpause', label: 'Trabpause', options: { type: 'switch', unitOptions: ["Distanz", "Zeit"] } },
  { id: 'abkuehlung', label: 'Abkühlung', options: { type: 'switch', unitOptions: ["Distanz", "Zeit"] } },
  { id: 'wiederholung', label: 'Wiederholung', options: { repetitions: 1 } }
];

function App() {
  const [droppedBlocks, setDroppedBlocks] = useState([]);
  const [instanceCounter, setInstanceCounter] = useState(0);
  const [unitName, setUnitName] = useState('');
  const [unitKuerzel, setUnitKuerzel] = useState('');
  const [savedUnits, setSavedUnits] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/trainingUnits')
      .then(res => res.json())
      .then(data => setSavedUnits(data))
      .catch(err => console.error(err));
  }, []);

  const handleDragEnd = (event) => {
    const { over, active } = event;
    if (!over) return;
    const dropAreaId = over.id;
    let parentId = null;
    if (dropAreaId !== 'drop-area') {
      parentId = dropAreaId.replace('drop-area-', '');
    }
    const newInstanceId = `inst-${instanceCounter}`;
    const newBlock = createNewBlock(active.data.current.block, newInstanceId);
    setDroppedBlocks(prev => addBlockToTree(prev, parentId, newBlock));
    setInstanceCounter(prev => prev + 1);
  };

  const handleDelete = (instanceId) => {
    setDroppedBlocks(prev => deleteBlockFromTree(prev, instanceId));
  };

  const handleMove = (instanceId, direction) => {
    setDroppedBlocks(prev => moveBlockInTree(prev, instanceId, direction));
  };

  const handleUpdateOptions = (instanceId, newOptions) => {
    setDroppedBlocks(prev => updateBlockOptionsInTree(prev, instanceId, newOptions));
  };

  const sums = computeSums(droppedBlocks);
  const sumText = `(${sums.distance}km + ${sums.time}min)`;

  const handleKuerzelChange = (e) => {
    setUnitKuerzel(e.target.value.replace(/\s/g, ''));
  };

  const handleSave = async () => {
    const trainingUnit = {
      name: unitName,
      kuerzel: unitKuerzel,
      blocks: droppedBlocks
    };
    try {
      let res = await fetch('http://localhost:3001/trainingUnits', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(trainingUnit)
      });
      if (res.status === 409) {
        const data = await res.json();
        if (window.confirm('Eine Einheit mit diesem Kürzel existiert bereits. Überschreiben?')) {
          res = await fetch(`http://localhost:3001/trainingUnits/${data.id}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(trainingUnit)
          });
          if (!res.ok) {
            alert('Fehler beim Überschreiben.');
            return;
          }
          alert('Trainingseinheit überschrieben!');
        } else {
          return;
        }
      } else if (!res.ok) {
        alert('Fehler beim Speichern.');
        return;
      } else {
        alert('Trainingseinheit gespeichert!');
      }
      const updated = await fetch('http://localhost:3001/trainingUnits');
      const data = await updated.json();
      setSavedUnits(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleLoad = (unit) => {
    setDroppedBlocks(unit.blocks);
    setUnitName(unit.name);
    setUnitKuerzel(unit.kuerzel);
  };

  return (
    <DndContext onDragEnd={handleDragEnd}>
      <div className="app-container">
        <Sidebar_SavedUnits savedUnits={savedUnits} onLoad={handleLoad} />
        
        <Mainbar
          unitName={unitName}
          onNameChange={(e) => setUnitName(e.target.value)}
          onKuerzelChange={handleKuerzelChange}
          onSave={handleSave}/>

        <Sidebar_Blocks availableBlocks={availableBlocks} />
      </div>
    </DndContext>
  );
}

export default App;
